import Input from "../Input";
import styled from 'styled-components'
import React, { useState } from "react";
import { livros } from "./dadosPesquisa";

const PesquisaContainer = styled.section`
    background-image: linear-gradient(90deg, #002F52 35%, #326589 165%);
    color: #FFF;
    text-align: center;
    padding: 85px 0;
    height: 470px;
    width: 100%;
`

const Titulo = styled.h2`
    color: #FFF;
    font-size: 36px;
    text-align: center;
    width: 100%;
`

const Subtitulo = styled.h3`
    font-size: 16px;
    font-weight: 500;
    margin-bottom: 40px;
`
const Resultado = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    cursor:pointer;
    margin-botton: 20px;
    transition: transform .2s;
    padding: 15px 0;
   

    p { 
        width: 200px;
    }

    img {
        width: 100px;
    }

    &:hover {
        transform: scale(1.1);
    }

`
/*
function Pesquisa() {
    const [livrosPesquisados, setLivrosPesquisados] = useState([]);
    console.log(livrosPesquisados);

    return (
        <PesquisaContainer>
            <Titulo>Pesquisa de Livros</Titulo>
            <Subtitulo>Encontre um livro em nossa estante</Subtitulo>
            <Input
                type="text"
                placeholder="Escreva sua próxima leitura"
                onChange={(evento) => {
                    const textoDigitado = evento.target.value;

                    // Atualize apenas se houver texto no campo de pesquisa
                    if (textoDigitado) {
                        const resultadoPesquisa = livros.filter((livro) =>
                            livro.nome.includes(textoDigitado)
                        );
                        setLivrosPesquisados(resultadoPesquisa);
                    } else {
                        // Se o campo de pesquisa estiver vazio, limpe os resultados
                        setLivrosPesquisados([]);
                    }
                }}
            />
            {livrosPesquisados.length > 0 && (
                <div>
                    <ul>
                        {livrosPesquisados.map((livro) => (
                            <Resultado>
                                <img src={livro.src}/>
                                <p>{livro.nome}</p>
                            </Resultado>
                        ))}
                    </ul>
                </div>
            )}
        </PesquisaContainer>
    );
}
